package Day1;

public class GreaterSmall {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int a=80;
      int b=49;
       if(a>b)
       {
    	   System.out.println("greater");
       }
       else if(a<b)
       {
    	   System.out.println("smaller");
       }
       else
       {
    	   System.out.println("equals");
       }
	}

}
